const CustomDropdown = ({ data, optionLabel = "", onChangeHandle, defaultValue }) => {

   return (
      <div className='relative'>
         <select onChange={onChangeHandle} value={defaultValue}
            className='p-2 border border-[#494C55] rounded-lg bg-transparent cursor-pointer text-transparent' style={{ width: '200px'}}>

            {data.map(option => (
               <option key={option.value} value={option.value} className="text-white bg-[#37393F]">
                  {option.text}
               </option>
            ))}
            <span className='absolute top-0 left-0 p-2 text-white whitespace-nowrap'>{optionLabel} {defaultValue} <i className='bi bi-chevron-down'></i></span>

         </select>
          </div>
   );
};

export default CustomDropdown
