const CustomDropdown = ({ data, optionLabel = "", onChangeHandle, defaultValue }) => {

   return (
      <div className='relative'>
         <select onChange={onChangeHandle} value={defaultValue}
            className='p-2 border border-[#494C55] rounded-lg bg-transparent w-fit min-w-[600px] cursor-pointer'>
            <option value="" disabled hidden>{optionLabel}</option>
            {data.map(option => (
               <option key={option.value} value={option.value} className="text-white bg-[#37393F]">
                  {option.text}
               </option>
            ))}
         </select>
         <span className='absolute top-0 left-0 p-2 bg-[#37393F] text-white'>{optionLabel}</span>
      </div>
   );
};

export default CustomDropdown
