const CustomDropdown = ({ data, onChangeHandle, defaultValue, optionLabel }) => {
   return (
      <div className='p-2 border border-[#494C55] rounded-lg bg-transparent w-fit relative'>
         <button className='text-left whitespace-nowrap' onClick={() => this.nextElementSibling.focus()}>
            {optionLabel}
         </button>
         <select onChange={onChangeHandle} value={defaultValue} className='absolute top-0 left-0 w-full p-2 bg-transparent'>
            {data.map(option => (
               <option key={option.value} value={option.value}>
                  {option.text}
               </option>
            ))}
         </select>
      </div>
   );
};

export default CustomDropdown
